mapper.select:
SELECT ps.requested_product_code AS rpCode ,pl.process_order_num AS orderNum ,ps.process_status AS pStatus ,ps.process_start_date AS pStartDate ,ps.completed_product_update_date AS pEndDate ,pl.production_process_high_classification AS phighClassification ,pl.production_process_low_classification AS plowClassification
FROM product_production_process_status AS ps INNER JOIN production_process_list AS pl ON ps.production_process_code = pl.production_process_code WHERE ps.requested_product_code = #{requestedProductCode};
${searchKey} LIKE CONCAT('%', #{searchValue} ,'%');
