mapper.select:
SELECT pps.production_process_code AS processCode, pps.requested_product_code AS productCode, DATE_FORMAT(pps.process_start_date, '%Y-%m-%d %T') AS processStartDate, DATE_FORMAT(pps.completed_product_update_date, '%Y-%m-%d %T') AS processFinishDate, pps.process_status AS processStatus, rpd.client_code AS clientCode, c.client_name AS clientName, rpd.requested_due_date AS dueDate, pd.gender_categorized_name AS suitType, pd.detailed_categorized_name AS productType, detail.production_process_high_classification AS highClassName, detail.production_process_low_classification AS lowClassName, detail.process_order_num AS processOrderNum FROM product_production_process_status AS pps INNER join requested_product_detail AS rpd on pps.requested_product_code = rpd.requested_product_code INNER join product_code_detail AS pd on pd.product_code = rpd.product_code INNER join client_info AS c ON rpd.client_code = c.client_code INNER join production_process_list AS detail on pps.production_process_code = detail.production_process_code
AND pd.gender_categorized_name LIKE CONCAT('%', #{suitType} ,'%')
AND pd.detailed_categorized_name LIKE CONCAT('%', #{productType} ,'%')
AND rpd.client_code LIKE CONCAT('%', #{clientCode} ,'%')
AND rpd.requested_due_date >= #{fromDate}
AND rpd.requested_due_date <= #{toDate}
AND pps.process_start_date >= #{fromDate}
AND pps.process_start_date <= #{toDate}

mapper.update:
UPDATE product_production_process_status
	
					
			,process_start_date = #{processStartDate}
		
					
			,completed_product_update_date = #{completedProductUpdateDate}
		
					
			,process_status = #{processStatus}
		
	
	WHERE 	
		requested_product_code=#{productCode}
		AND
		production_process_code = #{productionProcessCode};
